"use strict";function innerNavbar(){var e=document.querySelector("nav"),t=document.querySelector(".nav-links"),n=document.querySelector(".burger");768<window.outerWidth&&window.addEventListener("scroll",function(){70<window.pageYOffset?e.classList.add("active"):e.classList.remove("active")}),n.addEventListener("click",function(){t.classList.toggle("active"),n.classList.toggle("toggle")})}function innerLightbox(){var e=document.querySelectorAll(".img"),t=document.querySelectorAll(".close-btn"),o=document.querySelectorAll(".lightbox");e.forEach(function(e,n){e.addEventListener("click",function(){o.forEach(function(e,t){n===t&&(e.classList.add("active"),document.body.style.overflowY="hidden")})})}),t.forEach(function(e,n){e.addEventListener("click",function(){o.forEach(function(e,t){n===t&&(e.classList.remove("active"),document.body.style.overflowY="auto")})})})}innerNavbar(),innerLightbox();